import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
    *{
        padding: 0;
        margin: 0;
        
    }
    body{
        background: #cccc;
        font-family: 'Chango', cursive;
    }

    button{
    width: 80px;
    height: 35px;
    background-color: white;
    border-color: black;
    margin-left: 10px;
    }
    
    input{
        height: 31px;
        padding-left: 55px;
        border-color: black;
    }
`
